<!-- sidebar menu: : style can be found in sidebar.less -->                    <div class="user-panel">	                	<div class="pull-left info" style="text-align:center;">	                    	<p>目前時間</p>							<div id="_timer_"><?php echo date("Y-m-d H:i:s"); ?></div>						</div>	                </div>                    <ul class="sidebar-menu">                    	                    	<?php foreach($this -> menus as $menu_key => $menu): ?>                    	                    	                    	<?php if(count($menu -> items) > 0): ?>						<li class="treeview">                            <a href="#">                                <i class="fa fa-th-list"></i> <span><?php echo $menu -> title; ?></span>                                <i class="fa fa-angle-left pull-right"></i>                            </a>                                                        <ul class="treeview-menu">                            	                            	<?php foreach($menu -> items as $item_key => $item): ?>                            	<?php if($item -> moduleType == "Link"): ?>                            	<li <?php if($this -> module_io -> mod == $item -> code): ?>class="active"<?php endif; ?> id="<?php echo $item -> code; ?>"><a href="<?php echo $item -> content; ?>" target="<?php echo $item -> target; ?>"><i class="fa fa-angle-double-right"></i> <?php echo $item -> title; ?></a></li>                            	<?php else: ?>                                <li <?php if($this -> module_io -> mod == $item -> code): ?>class="active"<?php endif; ?> id="<?php echo $item -> code; ?>"><a href="list.php?mod=<?php echo $item -> code; ?>"><i class="fa fa-angle-double-right"></i> <?php echo $item -> title; ?></a></li>                                <?php endif; ?>                                                                <?php endforeach; ?>                            </ul>                        </li>                        <?php endif; ?>                                                <?php endforeach; ?>                    </ul>                    <script type="text/javascript">						$("document").ready(function() {							$(".active").parents(".treeview").addClass("active");						});						var menu_max_timer = <?php echo strtotime(date("Y-m-d H:i:s")) - strtotime(date("Y-m-d")); ?>;						function clock_run(){							menu_max_timer++;								hur = parseInt(menu_max_timer / 60 / 60);							min = parseInt(menu_max_timer / 60) % 60;							sec = menu_max_timer % 60;														hur = (hur < 10) ? ("0" + hur) : hur;							min = (min < 10) ? ("0" + min) : min;							sec = (sec < 10) ? ("0" + sec) : sec;							$("#_timer_").html("<?php echo date("Y-m-d"); ?> " + hur + ":" + min + ":" + sec);						}						interval = setInterval(clock_run,1000);                    </script>